#!/bin/bash
# Hunt for long-running benchmarks by timing out each one at 60 seconds
# Copyright (C) 2025 Acar, Blelloch and Milnes from 'Algorithms Parallel and Sequential'.

set -e
set -o pipefail

TIMEOUT=40
KILL_AFTER=5  # Grace period for cleanup before SIGKILL
TIMED_OUT_FILE="analyses/benchmarks_timed_out.txt"
SUCCESS_FILE="analyses/benchmarks_success.txt"
LOG_FILE="analyses/long_benchmark_hunt.txt"

# Function to run with graceful timeout (SIGTERM first, then SIGKILL after grace period)
run_with_timeout() {
    timeout --signal=TERM --kill-after=${KILL_AFTER} ${TIMEOUT} "$@"
}

# Clear previous results
> "$TIMED_OUT_FILE"
> "$SUCCESS_FILE"
> "$LOG_FILE"

# Redirect all output to log file and terminal
exec > >(tee "$LOG_FILE") 2>&1

echo "========================================================"
echo "Long Benchmark Hunt - Timeout: ${TIMEOUT}s per benchmark"
echo "Started: $(date '+%Y-%m-%d %H:%M:%S')"
echo "Log file: $LOG_FILE"
echo "========================================================"
echo ""

# Run each benchmark with timeout - NO LOOPS!

run_with_timeout cargo bench --bench BenchAdjTableGraphStPer -j 10 2>&1 | sed 's/\x1b\[[0-9;]*[mGKHJA-Z]//g' && echo "BenchAdjTableGraphStPer" >> "$SUCCESS_FILE" || { echo "TIMED OUT: BenchAdjTableGraphStPer"; echo "BenchAdjTableGraphStPer" >> "$TIMED_OUT_FILE"; }
run_with_timeout cargo bench --bench BenchArraySeq -j 10 2>&1 | sed 's/\x1b\[[0-9;]*[mGKHJA-Z]//g' && echo "BenchArraySeq" >> "$SUCCESS_FILE" || echo "BenchArraySeq" >> "$TIMED_OUT_FILE"
